import React from 'react';
import styled from 'styled-components';

const ChatHallBlock = styled.div`
  width: 800px;
  height: 500px;
  margin: 50px auto;
  display: flex;
  justify-content: space-between;
`;
const ChatListContainer = styled.div`
  width: 65%;
  height: 95%;
  border: 1px solid black;
  position: relative;
`;
const UserList = styled.div`
  width: 30%;
  height: 95%;
  border: 1px solid black;
`;
const ChatList = styled.div``;
const ChatInputBox = styled.div`
  width: 100%;
  position: absolute;
  left: 0;
  bottom: 10px;
  display: flex;
  justify-content: space-around;
  align-items: center;
`;
const ChatInput = styled.input`
  width: 70%;
  height: 25px;
`;
const ChatButton = styled.button`
  width: 50px;
  height: 30px;
  cursor: pointer;
  background: none;
  outline: none;
  border: 2px solid black;
  border-radius: 20px;
  font-size: 1.7rem;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const ChatHall = () => {
  return (
    <ChatHallBlock>
      <ChatListContainer>
        <ChatList></ChatList>
        <ChatInputBox>
          <ChatInput />
          <ChatButton>+</ChatButton>
        </ChatInputBox>
      </ChatListContainer>
      <UserList></UserList>
    </ChatHallBlock>
  );
};

export default ChatHall;
