import React, { useEffect, useState } from 'react';
import ChatHall from '../../components/common/ChatHall';
import { useSelector } from 'react-redux';
import { io } from 'socket.io-client';

const SERVER = 'http://localhost:4000';
let socket;

const ChatHallContainer = () => {
  const [msg, setMsg] = useState('');
  let { nickname, profileImage } = useSelector(({ user }) => ({
    nickname: user.user ? user.user.nickname : 'guest',
    profileImage: user.user ? user.user.profileImage : '',
  }));
  console.log(nickname, profileImage);
  useEffect(() => {});
  useEffect(() => {
    socket = io(SERVER);
    socket.emit('join', nickname);
    socket.on('reply', (arg) => console.log(`reply : ${arg}`));
  }, [nickname]);
  const onChange = (e) => {
    setMsg(e.target.value);
  };
  const onSubmit = () => {
    socket.emit('msg', msg);
    setMsg('');
  };
  const onKeySubmit = (e) => {
    if (e.key === 'Enter') {
      onSubmit();
      setMsg('');
    }
  };

  return (
    <ChatHall
      msg={msg}
      onChange={onChange}
      onSubmit={onSubmit}
      onKeySubmit={onKeySubmit}
    />
  );
};

export default ChatHallContainer;
