import React from 'react';
import styled from 'styled-components';
import Friend from './Friend';

const RecommandListContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  width: 25%;
`;
const RecommandListTitleContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const RecommandListTitle = styled.div`
  width: 50%;
  background: #73b2ff;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 7px;
  border-radius: 10px;
`;
const RefreshButton = styled.div`
  width: 22px;
  height: 22px;
  border-radius: 10px;
  border: 2px solid #73b2ff;
  background-size: 80% 80%;
  background-repeat: no-repeat;
  background-position: center;
  background-image: url('/img/refresh.png');
  margin-right: 10px;
  cursor: pointer;
`;
const RecommandListBlock = styled.div`
  width: 100%;
  height: 580px;
  border: 2px solid #73b2ff;
  border-radius: 10px;
  padding: 5px;
`;
const RecommandList = ({ onRefresh, recommandList }) => {
  return (
    <RecommandListContainer>
      <RecommandListTitleContainer>
        <RefreshButton onClick={onRefresh} />
        <RecommandListTitle>추천 목록</RecommandListTitle>
      </RecommandListTitleContainer>
      <RecommandListBlock>
        {recommandList.map((person) => (
          <Friend
            recommand
            key={person.uid}
            nickname={person.nickname}
            profileImage={person.profileImage}
            tags={person.tags}
          />
        ))}
      </RecommandListBlock>
    </RecommandListContainer>
  );
};

export default RecommandList;
