import React from 'react';
import styled from 'styled-components';
import palette from '../../lib/styles/palette';

const LoginBox = styled.div`
  width: 100vw;
  height: 100vh;
  margin: 0 auto;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background: #d3d3d3;
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 50px;
`;
const Login = styled.div`
  width: 400px;
  height: 350px;
  border-radius: 20px;
  border: 2px solid black;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
  flex-direction: column;
`;
const InputBox = styled.div`
  width: 80%;
  height: 35%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
`;
const StyledInput = styled.input`
  width: 80%;
  height: 10%;
  border: none;
  outline: none;
  border-bottom: 1px solid black;
  padding: 15px;
  font-size: 1.25rem;
`;

const ButtonBox = styled.div`
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-end;
  width: 70%;
  height: 25%;
`;

const LoginButton = styled.button`
  width: 100%;
  height: 50px;
  outline: none;
  border: 3px solid ${palette.blue[2]};
  color: ${palette.blue[2]};
  border-radius: 5px;
  font-size: 1.5rem;
  font-weight: bold;
  background: none;
  cursor: pointer;
`;
const RegisterButton = styled.button`
  width: 30%;
  outline: none;
  border: none;
  background: none;
  font-size: 1.125rem;
  color: gray;
`;

const LoginForm = () => {
  console.log(palette);
  return (
    <LoginBox>
      <Title>Connect에 오신 것을 환영합니다.</Title>
      <Login>
        <InputBox>
          <StyledInput type="text" placeholder="아이디" />
          <StyledInput type="password" placeholder="비밀번호" />
        </InputBox>
        <ButtonBox>
          <LoginButton>로그인</LoginButton>
          <RegisterButton>회원가입</RegisterButton>
        </ButtonBox>
      </Login>
    </LoginBox>
  );
};

export default LoginForm;
