import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import ProfileSettingForm from '../../components/auth/ProfileSettingForm';
import { setTempInfo } from '../../modules/auth';

const ProfileSettingContainer = () => {
  const dispatch = useDispatch();
  const { username, password, nickname, profileImage } = useSelector(
    ({ auth: { tempInfo } }) => ({
      username: tempInfo.username,
      password: tempInfo.password,
      nickname: tempInfo.nickname,
      profileImage: tempInfo.profileImage,
    }),
  );
  const onChange = (e, ref) => {
    if (e.target.files) {
      const reader = new FileReader();
      reader.readAsDataURL(e.target.files[0]);
      reader.onload = (event) => {
        ref.style.backgroundImage = `url(${event.target.result})`;
      };
      dispatch(setTempInfo({ type: 'profileImage', value: e.target.files[0] }));
    } else {
      console.log('아이디');
    }
  };
  console.log(username, password);

  return <ProfileSettingForm onChange={onChange} username={username} />;
};

export default ProfileSettingContainer;
