import React from 'react';
import styled from 'styled-components';

const ChattingInputBlock = styled.div`
  margin-top: 15px;
  width: 100%;
  display: flex;
  flex: 1;
`;
const ChattingInputLeftBox = styled.div`
  background: gray;
  width: 80%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;
const ChattingArea = styled.textarea`
  resize: none;
  height: 60%;
  border: none;
  background: pink;
`;
const ChattingContentsArea = styled.div`
  display: flex;
`;
const ChattingInputRightBox = styled.div``;
const Icon = styled.div`
  width: 30px;
  height: 30px;
  background-size: 60% 60%;
  background-repeat: no-repeat;
  background-position: center center;
  cursor: pointer;
`;
const Emoticon = styled(Icon)`
  background-image: url('/img/Emoticon.png');
`;
const Picture = styled(Icon)`
  background-image: url('/img/Picture.svg');
`;
const File = styled(Icon)`
  background-image: url('/img/Clip.png');
`;

const ChattingInput = () => {
  return (
    <ChattingInputBlock>
      <ChattingInputLeftBox>
        <ChattingArea />
        <ChattingContentsArea>
          <Emoticon />
          <Picture />
          <File />
        </ChattingContentsArea>
      </ChattingInputLeftBox>
      <ChattingInputRightBox></ChattingInputRightBox>
    </ChattingInputBlock>
  );
};

export default ChattingInput;
