import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import RecommandList from '../../components/Friends/RecommandList';
import { getRecommand } from '../../modules/friends';

const RecommandContainer = () => {
  const dispatch = useDispatch();
  const { recommandList, tags } = useSelector(
    ({ user, friends: { recommandList, tagList } }) => ({
      recommandList,
      tags: tagList,
      uid: user.user.uid,
    }),
  );
  useEffect(() => {
    dispatch(getRecommand(tags, uid));
  }, [dispatch, tags]);
  return <RecommandList recommandList={recommandList} />;
};

export default RecommandContainer;
