import React from 'react';
import styled, { css } from 'steyld-component';

const ChatMessageBlock = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  ${({ received }) =>
    received &&
    css`
      justify-content: flex-start;
    `}
  ${({ send }) =>
    send &&
    css`
      justify-content: flex-end;
    `}
`;

const ChatMessageBox = styled.div`
  display: flex;
  width: 100%;
  ${({ type }) =>
    type === 'received' &&
    css`
      text-align: left;
      color: blue;
    `}
  ${({ type }) =>
    type === 'send' &&
    css`
      text-align: right;
      color: red;
      justify-content: flex-end;
    `}
`;
const Nickname = styled.div`
  color: white;
  padding: 10px;
  border-radius: 12px;
  margin-bottom: 10px;
  margin-right: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const Text = styled.div`
  line-height: 1rem;
  padding: 10px;
  margin-bottom: 10px;
  word-wrap: break-word;
  max-width: 50%;
  ${({ type }) =>
    type === 'received' &&
    css`
      background: #d0f8ff;
      border-radius: 15px;
    `}

  ${(props) =>
    props.send &&
    css`
      border: 1.5px solid brown;
      background: pink;
      border-radius: 15px;
    `}
`;
const ChatMessage = ({ idx, type, nickname, profileImage, msg }) => {
  return (
    <ChatMessageBlock type={type} key={idx}>
      <ChatMessageBox type={type}>
        {type === 'received' ? <Nickname>{nickname}</Nickname> : ''}
        <Text type={type}>{msg}</Text>
      </ChatMessageBox>
    </ChatMessageBlock>
  );
};

export default ChatMessage;
