import styled, { css } from 'styled-components';
import React from 'react';

const OptionContainer = styled.div`
  position: absolute;
  z-index: 2;
  width: 170px;
  height: 40px;
  border: 2px solid #73b2ff;
  border-radius: 12px;
  background: white;
  ${({ type }) =>
    type === 'recommand'
      ? css`
          left: 110%;
        `
      : css`
          left: -95%;
        `}
`;

const OptionBox = () => {
  return <OptionContainer></OptionContainer>;
};

export default OptionBox;
