import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import ProfileSettingForm from '../../components/auth/ProfileSettingForm';
import { initializeForm, setTempInfo, register } from '../../modules/auth';
import imageCompression from '../../../node_modules/browser-image-compression/dist/browser-image-compression';
import { useNavigate } from '../../../node_modules/react-router/index';

const ProfileSettingContainer = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [profileImage, setProfileImage] = useState(null);
  const [nickname, setNickname] = useState('');
  const { username, password } = useSelector(({ auth: { tempInfo } }) => ({
    username: tempInfo.username,
    password: tempInfo.password,
  }));
  const onChange = (e, ref) => {
    if (e.target.files) {
      const reader = new FileReader();
      reader.readAsDataURL(e.target.files[0]);
      reader.onload = (event) => {
        ref.style.backgroundImage = `url(${event.target.result})`;
        // setProfileImage(event.target.result.toString());
      };
      imageCompression
        .getDataUrlFromFile(e.target.files[0], {
          maxSizeMB: 1,
          maxWidthOrHeight: 380,
          userWebWorker: true,
        })
        .then((res) => {
          console.log(res);
          setProfileImage(res);
        });
    } else {
      setNickname(e.target.value);
    }
  };
  const onSubmit = (e) => {
    e.preventDefault();
    dispatch(register({ username, password, profileImage, nickname }));
  };
  useEffect(() => {
    if (username === '') {
      navigate(-1);
    }
    dispatch(initializeForm({ username, password }));
  }, [dispatch, password, username, navigate]);
  return (
    <ProfileSettingForm
      onChange={onChange}
      username={username}
      onSubmit={onSubmit}
    />
  );
};

export default ProfileSettingContainer;
