import React from 'react';
import styled from 'styled-components';

const ChatInfoBlock = styled.div`
  border-bottom: 1px solid #cdcdcd;
  width: 95%;
  height: 60px;
  margin: 3px auto;
  &:last-child {
    border: none;
  }
  display: flex;
  align-items: center;
`;
const ProfileImage = styled.div`
  width: 50px;
  height: 50px;
  border: 2px solid blue;
  border-radius: 10px;
  margin-right: 10px;
`;

const ChatName = styled.div`
  background: skyblue;
  max-width: 20%;
  overflow: hidden;
  color: white;
  padding: 10px;
  border-radius: 10px;
  margin-right: 10px;
`;

const LastMessage = styled.div`
  background: pink;
  padding: 10px;
  border-radius: 10px;
`;
const Time = styled.div`
  color: gray;
  letter-spacing: -1;
  font-size: 0.7rem;
`;
const ChatInfo = ({
  nickname,
  profileImage,
  chat_name,
  last_message,
  created_at,
}) => {
  function time(time) {
    const today = new Date();
    time = new Date(time);

    let year = today.getFullYear() - time.getFullYear();
    if (year !== 0) {
      return `${year}년 전`;
    }
    let month = today.getMonth() - time.getMonth();
    if (month !== 0) {
      return `${month}달 전`;
    }
    let day = today.getDate() - time.getDate();
    if (day !== 0) {
      return `${day}일 전`;
    }
    let meridiem = time.getHours() < 12 ? '오전 ' : '오후 ';
    let hour = time.getHours();
    if (hour === 0) hour = 12;
    if (hour > 12) hour -= 12;

    return meridiem + hour + '시 ' + time.getMinutes() + '분';
  }
  return (
    <ChatInfoBlock>
      <ProfileImage profileImage={profileImage} />
      <ChatName>{chat_name ? chat_name : nickname}</ChatName>
      <LastMessage title={last_message}>
        {last_message.slice(0, 15)}
      </LastMessage>
      <Time>{time(created_at)}</Time>
    </ChatInfoBlock>
  );
};

export default ChatInfo;
