import React, { useCallback, useEffect, useReducer, useState } from 'react';
import ChatHall from '../../components/common/ChatHall';
import { useDispatch, useSelector } from 'react-redux';
import io from 'socket.io-client';
import { socket } from '../../lib/sockets/chatHallSocket';
import { access, leave } from '../../lib/api/chatHall';
import { accessChatHall, getGuestNumber } from '../../modules/user';

const ChatHallContainer = () => {
  const [msg, setMsg] = useState('');
  const dispatch = useDispatch();

  const { user, userList } = useSelector(({ user, chatHallUserList }) => ({
    user: user,
    userList: chatHallUserList,
  }));

  useEffect(() => {
    if (!user) {
      dispatch(getGuestNumber());
    }
  });

  useEffect(() => {
    socket.on('broadcastMsg', (data) => {
      console.log('receive data : ', data);
      dispatch({ type: 'msg', msg: { ...data, received: true } });
    });

    return () => {};
  }, [dispatch]);

  const accessChatHall = async ({ username, nickname, profileImage }) => {
    const response = await access({ username, nickname, profileImage });
    const { guestInfo, list } = response.data;
    console.log(guestInfo, list);
    if (guestInfo) {
      dispatch({ type: 'set_guest_info', guestInfo });
    }
    dispatch({ type: 'set_user_list', list });
  };
  const leaveChatHall = async ({ username, nickname }) => {
    const response = await leave({ username, nickname });
  };

  const onChange = (e) => {
    setMsg(e.target.value);
  };

  const changeScroll = useCallback((e) => {
    e.scrollTop = e.scrollHeight;
  }, []);

  const onSubmit = () => {
    if (msg === '') return;
    socket.emit('msg', { nickname: user.nickname, msg: msg.trim() });
    dispatch({
      type: 'msg',
      msg: { send: true, nickname: user.nickname, msg: msg.trim() },
    });
    setMsg('');
  };

  const onKeySubmit = (e) => {
    if (e.key === 'Enter') {
      onSubmit();
      setMsg('');
    }
  };

  return (
    <ChatHall
      changeScroll={changeScroll}
      msg={msg}
      onChange={onChange}
      onSubmit={onSubmit}
      onKeySubmit={onKeySubmit}
      msgList={state.msg}
      userList={state.userList}
    />
  );
};

const initialState = {
  msg: [],
  myInfo: {
    username: null,
    nickname: null,
    profileImage: null,
  },
  userList: [],
};
function reducer(state, action) {
  switch (action.type) {
    case 'msg': {
      return { ...initialState, msg: [...state.msg, action.msg] };
    }
    case 'set_guest_info': {
      const { username, nickname } = action.guestInfo;
      console.log(username, nickname);
      return {
        ...state,
        myInfo: {
          username: username,
          nickname: nickname,
          profileImage: null,
        },
      };
    }
    case 'set_user_list': {
      const { list } = action;
      return {
        ...state,
        userList: [...list],
      };
    }
    default:
      return state;
  }
}

export default ChatHallContainer;
