import React from 'react';
import styled, { css } from 'styled-components';
import palette from '../../lib/styles/palette';

const MessageContainer = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  ${({ received }) =>
    received &&
    css`
      justify-content: flex-start;
    `}
  ${({ send }) =>
    send &&
    css`
      justify-content: flex-end;
    `}
`;
const MessageBlock = styled.div`
  display: flex;
  justify-content: space-between;
  max-width: 60%;
  ${(props) =>
    props.received &&
    css`
      text-align: left;
      color: blue;
    `}
  ${(props) =>
    props.send &&
    css`
      text-align: right;
      color: red;
    `}
`;
const Nickname = styled.div`
  color: white;
  background: ${palette.blue[1]};
  padding: 10px;
  border-radius: 12px;
  margin-bottom: 10px;
  margin-right: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const Text = styled.div`
  line-height: 1rem;
  padding: 10px;
  margin-bottom: 10px;
  word-wrap: break-word;
  width: 100%;
  ${(props) =>
    props.received &&
    css`
      border: 1.5px solid ${palette.blue[0]};
      background: #d0f8ff;
      border-radius: 15px;
    `}

  ${(props) =>
    props.send &&
    css`
      border: 1.5px solid brown;
      background: pink;
      border-radius: 15px;
    `}
`;

const Message = ({ idx, send, received, nickname, msg }) => {
  return (
    <MessageContainer send={send} received={received} key={idx}>
      <MessageBlock received={received}>
        {received ? <Nickname>{nickname}</Nickname> : ''}
        <Text send={send} received={received}>
          {msg}
        </Text>
      </MessageBlock>
    </MessageContainer>
  );
};

export default Message;
