import React from 'react';
import styled from 'styled-components';

const ChattingInputBlock = styled.form`
  margin-top: 15px;
  width: 100%;
  display: flex;
  flex: 1;
`;
const ChattingInputLeftBox = styled.div`
  width: 80%;
  height: 100%;
  display: flex;
  flex-direction: column;
`;
const ChattingArea = styled.textarea`
  resize: none;
  height: 60%;
  border: none;
  background: pink;
  outline: none;
`;
const ChattingContentsArea = styled.div`
  display: flex;
`;
const Icon = styled.div`
  width: 23px;
  height: 23px;
  margin-right: 10px;
  background-size: 100% 100%;
  background-repeat: no-repeat;
  background-position: center center;
  cursor: pointer;
`;
const Emoticon = styled(Icon)`
  background-image: url('/img/Emoticon.png');
`;
const Picture = styled(Icon)`
  background-image: url('/img/Picture.svg');
`;
const File = styled(Icon)`
  background-image: url('/img/Clip.png');
`;
const ChattingInputRightBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  margin-top: 10px;
  width: 20%;
  height: 100%;
`;

const Submit = styled.button`
  background: #73b2ff;
  color: white;
  outline: none;
  border: none;
  border-radius: 10px;
  padding: 8px 15px;
  letter-spacing: 2px;
  cursor: pointer;
  text-align: center;
`;

const ChattingInput = () => {
  return (
    <ChattingInputBlock>
      <ChattingInputLeftBox>
        <ChattingArea />
        <ChattingContentsArea>
          <Emoticon />
          <Picture />
          <File />
        </ChattingContentsArea>
      </ChattingInputLeftBox>
      <ChattingInputRightBox>
        <Submit>전송</Submit>
      </ChattingInputRightBox>
    </ChattingInputBlock>
  );
};

export default ChattingInput;
