import React, { useState } from 'react';
import TagList from '../../components/Friends/TagList';

const TagContainer = () => {
  const [tags, setTags] = useState(['축구', '야구', '영화', '낮잠']);
  const onSubmit = (e) => {
    e.preventDefault();
    const match = /^[ㄱ-ㅎ|가-힣|a-z|A-Z|0-9|\s]/;
    const tag = e.target.tag.value.replaceAll(match, '');
    e.target.tag.value = '';
    if (tags.includes(tag)) {
      e.target.tag.value = '';
      return;
    }

    setTags([...tags, tag]);
  };
  const onRemove = (e) => {
    console.log(e.target.textContent);
    setTags(tags.filter((tag) => tag !== e.target.textContent.slice(1)));
  };
  return <TagList onSubmit={onSubmit} onRemove={onRemove} tags={tags} />;
};

export default TagContainer;
