import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import TagList from '../../components/Friends/TagList';
import { addTag, loadTags } from '../../modules/friends';

const TagContainer = () => {
  const dispatch = useDispatch();
  const {
    user: { uid },
    tags,
  } = useSelector(({ friends, user }) => ({
    tags: friends.tagList.split('#').filter((el) => el !== ''),
    user: user.user,
  }));
  useEffect(() => {
    if (uid) {
      dispatch(loadTags(uid));
    }
  }, [dispatch, uid]);
  const onSubmit = (e) => {
    e.preventDefault();

    const match = /[^ㄱ-ㅎ|가-힣|a-z|A-Z|0-9|]/g;
    const tag = e.target.tag.value.replace(match, '');
    e.target.tag.value = '';
    if (tags.includes(tag) || tag === '') {
      e.target.tag.value = '';
      return;
    }
    dispatch(addTag({ tag }));
  };
  const onRemove = (e) => {
    console.log(e.target.textContent);
  };
  return <TagList onSubmit={onSubmit} onRemove={onRemove} tags={tags} />;
};

export default TagContainer;
