import React, { useState } from 'react';
import ChattingInput from '../../components/Chatting/ChattingInput';
import { socket } from '../../lib/sockets/chatHallSocket';
const ChattingInputContainer = ({ other }) => {
  const [textArea, setTextArea] = useState('');
  const onPressEnter = (e) => {
    if (e.key === 'Enter') {
      onSubmit(e);
    }
  };
  const onChange = (e) => {
    setTextArea(e.target.value);
  };
  const onSubmit = (e) => {
    e.preventDefault();
    socket.emit('send_message', { other, message: textArea });
    setTextArea('');
  };
  return (
    <ChattingInput
      onSubmit={onSubmit}
      onPressEnter={onPressEnter}
      onChange={onChange}
      text={textArea}
    />
  );
};
export default ChattingInputContainer;
