import io from 'socket.io-client';

let uid = localStorage.user ? JSON.parse(localStorage.user).uid : null;
const opts = {};
opts['sync disconnect on unload'] = false;
opts.query = { uid };
export const socket = io('http://localhost:4000', opts);
