import React from 'react';
import styled from 'styled-components';
import Background from '../common/Background';
const ProfileForm = styled.form`
  width: 400px;
  height: 650px;
  border-radius: 20px;
  border: 3px solid black;
  background: white;
  margin-top: 30px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ImageBox = styled.div`
  width: 330px;
  height: 330px;
  border: 2px solid black;
  border-radius: 20px;
  margin-top: 35px;
  background: url('./img/default_profile.jpg');
  background-size: 100% 100%;
`;
const UploadLabel = styled.label`
  outline: none;
  border: 2px solid black;
  width: 330px;
  font-size: 1.5rem;
  cursor: pointer;
  border-radius: 10px;
  margin-top: 35px;
  height: 45px;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const NicknameContainer = styled.div`
  width: 330px;
  display: flex;
  justify-content: space-between;
`;
const ProfileSettingForm = ({ onChange, username }) => {
  console.log(username);
  return (
    <>
      <Background>
        <h1>프로필 설정</h1>
        <ProfileForm>
          <ImageBox />
          <UploadLabel htmlFor="profile">프로필 선택</UploadLabel>
          <input
            onChange={onChange}
            id="profile"
            type="file"
            accept="image/*"
            style={{ display: 'none' }}
          />
          <NicknameContainer>
            <div style={{ width: '50px' }}>닉네임</div>
            <input type="text" placeholder={username} />
          </NicknameContainer>
        </ProfileForm>
      </Background>
    </>
  );
};

export default ProfileSettingForm;
