import React, { useState } from 'react';
import styled from 'styled-components';
import Friend from './Friend';

const FriendsListContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-direction: column;
  width: 22%;
`;
const FriendsListTitle = styled.div`
  width: 50%;
  background: #73b2ff;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 7px;
  border-radius: 10px;
`;
const FriendsListBlock = styled.div`
  width: 100%;
  height: 580px;
  border: 2px solid #73b2ff;
  border-radius: 10px;
  padding: 5px;
  overflow-y: auto;
  overflow-x: overlay;
  &::-webkit-scrollbar {
    width: 6px;
    height: 8px;
    border-radius: 6px;
    background: rgba(0, 0, 0, 0.1);
  }
  &::-webkit-scrollbar-thumb {
    width: 6px;
    height: 8px;
    border-radius: 6px;
    background: rgba(0, 0, 0, 0.4);
  }
`;

const friendsList = [
  {
    profileImage: '1646621980718.jpeg',
    nickname: '123',
    tags: '가_나_다_라',
  },
  { profileImage: '1646702364540.jpeg', nickname: 'nani' },
  { nickname: 'abcd', tags: '축구_자전거_보드게임' },
];

const FriendsList = () => {
  return (
    <FriendsListContainer>
      <FriendsListTitle>친구 목록</FriendsListTitle>
      <FriendsListBlock>
        {friendsList.map(({ profileImage, nickname, tags }) => (
          <Friend
            key={nickname}
            profileImage={profileImage}
            nickname={nickname}
            tags={tags}
          />
        ))}
      </FriendsListBlock>
    </FriendsListContainer>
  );
};

export default FriendsList;
