import React from 'react';
import styled, { css } from 'styled-components';

const ChatMessageBlock = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  ${({ received }) =>
    received &&
    css`
      justify-content: flex-start;
    `}
  ${({ send }) =>
    send &&
    css`
      justify-content: flex-end;
    `}
`;

const ChatMessageBox = styled.div`
  display: flex;
  width: 100%;
  ${({ type }) =>
    type === 'received' &&
    css`
      text-align: left;
      color: blue;
    `}
  ${({ type }) =>
    type === 'send' &&
    css`
      text-align: right;
      color: #a30000;
      justify-content: flex-end;
    `}
`;
const Nickname = styled.div`
  color: white;
  padding: 10px;
  border-radius: 12px;
  margin-bottom: 10px;
  margin-right: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #73b2ff;
`;
const Text = styled.div`
  line-height: 1rem;
  padding: 10px;
  margin-bottom: 10px;
  word-wrap: break-word;
  max-width: 50%;
  ${({ type }) =>
    type === 'received' &&
    css`
      background: #d0f8ff;
      border-radius: 15px;
    `}

  ${({ type }) =>
    type === 'send' &&
    css`
      border: 1.5px solid brown;
      background: #ffe3e7;
      border-radius: 15px;
    `}
`;
const TimeBlock = styled.div``;

const Time = ({ time }) => {
  const timeToString = ({ year, month, day, hour, minute }) => {
    let result = '';
    const today = new Date();
    if (today.getFullYear() !== year) {
      result += `${year}년 `;
    }
    if (today.getMonth() + 1 !== month) {
      result += `${today.getMonth() + 1}월\n`;
    }
    if (hour < 12) {
      result += `오전 ${hour === 0 ? 12 : hour}시 `;
    } else {
      result += `오후 ${hour === 24 ? 12 : hour - 12}시 `;
    }

    result += minute + '분';

    return result;
  };
  return <TimeBlock>{timeToString(time)}</TimeBlock>;
};

const ChatMessage = ({ type, nickname, msg, time }) => {
  console.log(time);
  return (
    <ChatMessageBlock type={type}>
      <ChatMessageBox type={type}>
        {type === 'received' ? <Nickname>{nickname}</Nickname> : ''}
        {type === 'send' ? <Time time={time} /> : ''}
        <Text type={type}>{msg}</Text>
        {type === 'received' ? <Time time={time} /> : ''}
      </ChatMessageBox>
    </ChatMessageBlock>
  );
};

export default ChatMessage;
