import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import RecommandList from '../../components/Friends/RecommandList';
import { getRecommand } from '../../modules/friends';

const RecommandContainer = () => {
  const dispatch = useDispatch();
  const { recommandList, tags } = useSelector(
    ({ friends: { recommandList, tagList } }) => ({
      recommandList,
      tags: tagList,
    }),
  );
  useEffect(() => {
    dispatch(getRecommand(tags));
  }, [dispatch]);
  return <RecommandList />;
};

export default RecommandContainer;
