import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const UserListBlock = styled.div`
  width: 30%;
  height: 100%;
  border: 1px solid black;
  overflow-y: auto;
  text-align: right;
  padding-right: 10px;
  float: right;
  &::-webkit-scrollbar {
    width: 6px;
    height: 8px;
    border-radius: 6px;
    background: rgba(0, 0, 0, 0.1);
  }
  &::-webkit-scrollbar-thumb {
    width: 6px;
    height: 8px;
    border-radius: 6px;
    background: rgba(0, 0, 0, 0.4);
  }
`;
const User = styled.div`
  display: flex;
  align-items: center;
  height: 50px;
`;
const UserProfile = styled.div`
  width: 30px;
  height: 30px;
  border: 3px solid red;
`;

const UserList = ({ userList, changeScroll }) => {
  console.log(userList);
  const scrollBox = useRef();
  useEffect(() => {
    changeScroll(scrollBox.current);
  }, [scrollBox, changeScroll]);
  return (
    <UserListBlock ref={scrollBox}>
      {userList &&
        userList.map((user, idx) => (
          <User key={idx} profileImage={user.profileImage}>
            {user.profileImage ? (
              <UserProfile profileImage={user.profileImage} />
            ) : (
              ''
            )}
            {user.nickname}
          </User>
        ))}
    </UserListBlock>
  );
};

export default UserList;
