import styled, { css } from 'styled-components';
import React from 'react';

const OptionContainer = styled.div`
  position: absolute;
  z-index: 2;
  width: 170px;
  height: 40px;
  border: 2px solid #73b2ff;
  border-radius: 12px;
  background: white;
  ${({ type }) =>
    type === 'recommand'
      ? css`
          left: 110%;
        `
      : css`
          left: -95%;
        `}
`;
const Chat = styled.div`
  width: 35px;
  height: 35px;
  background-image: url('/img/chat.png');
  border: 2px solid #737373;
`;

const OptionBox = ({ type }) => {
  return (
    <OptionContainer type={type}>
      {type === 'recommand' ? (
        <div>
          <Chat title="채팅하기" />
          <div>추가</div>
          <div>삭제</div>
        </div>
      ) : (
        <div>
          <div>채팅</div>
          <div>삭제</div>
        </div>
      )}
    </OptionContainer>
  );
};

export default OptionBox;
