import styled, { css } from 'styled-components';
import React from 'react';

const OptionContainer = styled.div`
  position: absolute;
  z-index: 2;
  width: 170px;
  height: 40px;
  border: 2px solid #73b2ff;
  border-radius: 12px;
  background: white;
  ${({ type }) =>
    type === 'recommand'
      ? css`
          left: 110%;
        `
      : css`
          left: -95%;
        `}
`;

const OptionBox = ({ type }) => {
  return (
    <OptionContainer type={type}>
      {type === 'recommand' ? (
        <div>
          <div>채팅</div>
          <div>추가</div>
          <div>삭제</div>
        </div>
      ) : (
        <div>
          <div>채팅</div>
          <div>삭제</div>
        </div>
      )}
    </OptionContainer>
  );
};

export default OptionBox;
