import React from 'react';
import styled from 'styled-components';

const ChatInfoBlock = styled.div`
  border-bottom: 1px solid #cdcdcd;
  width: 95%;
  height: 60px;
  margin: 3px auto;
  &:last-child {
    border: none;
  }
  display: flex;
  align-items: center;
`;
const ProfileImage = styled.div`
  width: 50px;
  height: 50px;
  border: 2px solid blue;
  border-radius: 10px;
  margin-right: 10px;
`;

const ChatName = styled.div`
  background: skyblue;
  max-width: 20%;
  overflow: hidden;
  color: white;
  padding: 10px;
  border-radius: 10px;
  margin-right: 10px;
`;

const LastMessage = styled.div`
  background: pink;
  padding: 10px;
  border-radius: 10px;
`;
const ChatInfo = ({ nickname, profileImage, chat_name, last_message }) => {
  return (
    <ChatInfoBlock>
      <ProfileImage profileImage={profileImage} />
      <ChatName>{chat_name ? chat_name : nickname}</ChatName>
      <LastMessage title={last_message}>
        {last_message.slice(0, 10)}
      </LastMessage>
    </ChatInfoBlock>
  );
};

export default ChatInfo;
