import React, { useEffect, useState } from 'react';
import ChatHall from '../../components/common/ChatHall';
import { useSelector } from 'react-redux';
import { io } from 'socket.io-client';

const SERVER = 'http://localhost:4000';
let socket = io(SERVER);

const ChatHallContainer = () => {
  const [msg, setMsg] = useState('');
  const [msgList, setMsgList] = useState([]);

  let { nickname, profileImage } = useSelector(({ user }) => ({
    nickname: user.user ? user.user.nickname : 'guest',
    profileImage: user.user ? user.user.profileImage : '',
  }));
  console.log(msgList);
  useEffect(() => {
    socket.on('broadcastMsg', (data) => {
      setMsgList([...msgList, { received: true, msg: data.msg }]);
    });
  }, [nickname, msgList, msg]);
  // useEffect(() => {
  //   return () => {
  //     socket.close();
  //   };
  // });
  const onChange = (e) => {
    setMsg(e.target.value);
  };
  const onSubmit = () => {
    socket.emit('msg', { nickname, msg: msg.trim() });
    setMsgList([...msgList, { send: true, msg: msg.trim() }]);
    setMsg('');
  };
  const onKeySubmit = (e) => {
    if (e.key === 'Enter') {
      onSubmit();
      setMsg('');
    }
  };

  return (
    <ChatHall
      msg={msg}
      onChange={onChange}
      onSubmit={onSubmit}
      onKeySubmit={onKeySubmit}
      msgList={msgList}
    />
  );
};

export default ChatHallContainer;
