import React, { useCallback, useEffect, useReducer, useState } from 'react';
import ChatHall from '../../components/common/ChatHall';
import { useSelector } from 'react-redux';
import io from 'socket.io-client';
import { socket } from '../../lib/sockets/chatHallSocket';
import { access, leave } from '../../lib/api/chatHall';

const SERVER = 'http://localhost:4000';
const ChatHallContainer = () => {
  const [msg, setMsg] = useState('');
  const [state, dispatch] = useReducer(reducer, initialState);

  let { username, nickname, profileImage } = useSelector(({ user }) => ({
    username: user.user ? user.user.username : 'guest',
    nickname: user.user ? user.user.nickname : 'guest',
    profileImage: user.user ? user.user.profileImage : '',
  }));
  useEffect(() => {
    socket.on('broadcastMsg', (data) => {
      console.log('receive data : ', data);
      dispatch({ type: 'msg', msg: { ...data, received: true } });
    });

    if (username && nickname) {
      accessChatHall({ username, nickname, profileImage });
    }

    return () => {
      leaveChatHall({ username, nickname });
    };
  }, [dispatch, username, nickname, profileImage]);

  const accessChatHall = async ({ username, nickname, profileImage }) => {
    const response = await access({ username, nickname, profileImage });
    const { guestInfo, list } = response.data;
    console.log(guestInfo, list);
    if (guestInfo) {
      dispatch({ type: 'set_guest_info', guestInfo });
    }
  };
  const leaveChatHall = async ({ username, nickname }) => {
    const response = await leave({ username, nickname });
  };

  const onChange = (e) => {
    setMsg(e.target.value);
  };

  const changeScroll = useCallback((e) => {
    e.scrollTop = e.scrollHeight;
  }, []);

  const onSubmit = () => {
    if (msg === '') return;
    socket.emit('msg', { nickname, msg: msg.trim() });
    dispatch({ type: 'msg', msg: { send: true, nickname, msg: msg.trim() } });
    setMsg('');
  };

  const onKeySubmit = (e) => {
    if (e.key === 'Enter') {
      onSubmit();
      setMsg('');
    }
  };

  return (
    <ChatHall
      changeScroll={changeScroll}
      msg={msg}
      onChange={onChange}
      onSubmit={onSubmit}
      onKeySubmit={onKeySubmit}
      msgList={state.msg}
      userList={state.userList}
    />
  );
};

const initialState = {
  msg: [],
  myInfo: {
    username: 'guest',
    nickname: 'guest',
    profileImage: null,
  },
  userList: [],
};
function reducer(state, action) {
  if (action.type === 'msg') {
    return { ...initialState, msg: [...state.msg, action.msg] };
  } else if (action.type === 'setting') {
    console.log('in action.type setting', state, action);
    return {
      ...initialState,
      myInfo: { nickname: action.nickname, profileImage: action.profileImage },
    };
  } else if (action.type === 'set_guest_info') {
    const { username, nickname } = action.guestInfo;
    return {
      ...state,
      myInfo: {
        username: username,
        nickname: nickname,
        profileImage: null,
      },
    };
  } else if (action.type === 'access') {
    console.log(state);
    return {
      ...state.myInfo,
    };
  } else {
    throw new Error();
  }
}

export default ChatHallContainer;
