import React from 'react';
import styled from 'styled-components';

const FriendBlock = styled.div`
  margin: 0 auto;
  width: 92%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 50px;
  border-radius: 10px;
  padding: 4px;
  transition: 0.3s;
  &:hover {
    background: #73b2ff;
  }
  position: relative;
`;
const ProfileImage = styled.div`
  width: 40px;
  height: 40px;
  border: 2px solid black;
  border-radius: 10px;
  background-size: 100% 100%;
  ${({ profileImage }) =>
    profileImage
      ? `background-image : url(${profileImage});`
      : `background-image : url('/img/default_profile.jpg');`}
`;

const Nickname = styled.div`
  width: 80px;
  height: 40px;
  border: 2px solid black;
  border-radius: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: white;
  overflow: hidden;
  padding: 0 10px;
  text-overflow: ellipsis;
  white-space: nowrap;
`;
const HoverArea = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 100%;
  border-radius: 10px;
  padding: 4px;
  transition: 0.3s;
`;
const TagsContainer = styled.div`
  position: absolute;
  width: 100%;
  border: 2px solid #ff6e87;
  border-radius: 12px;
  left: 110%;
  color: white;
  display: flex;
  flex-wrap: wrap;
  transition: 0.3s;
  opacity: 0;
  animation-duration: 0.7s;
  animation-name: tags;
  animation-iteration-count: infinite;
  animation-direction: alternate-reverse;
  @keyframes tags {
    from {
      top: 23px;
    }
    to {
      top: 27px;
    }
  }

  ${HoverArea}:hover ~ & {
    opacity: 1;
  }
`;
const Tag = styled.div`
  background: pink;
  margin: 5px;
  border-radius: 5px;
  padding: 0 5px;
`;

const Friend = ({ profileImage, nickname, tags }) => {
  return (
    <FriendBlock>
      <HoverArea>
        <ProfileImage profileImage={profileImage} />
        <Nickname>{nickname}</Nickname>
      </HoverArea>
      <TagsContainer>
        {tags.length !== 0
          ? tags.split('_').map((tag) => <Tag key={tag}>{tag}</Tag>)
          : ''}
      </TagsContainer>
    </FriendBlock>
  );
};

export default Friend;
