import { createAction, handleActions } from 'redux-actions';
import * as authAPI from '../lib/api/auth';
import { takeLatest } from 'redux-saga/effects';
import createRequestSaga, {
  createRequestActionTypes,
} from '../lib/createRequestSaga';

const CHANGE_FIELD = 'auth/CHANGE_FIELD';
const INITIALIZE_FORM = 'auth/INITIALIZE_FORM';
const INITIALIZE_TEMP_INFO = 'auth/INITIALIZE_TEMP_INFO';
const SET_TEMP_INFO = 'auth/SET_TEMP_INFO';
const [REGISTER, REGISTER_SUCCESS, REGISTER_FAILURE] =
  createRequestActionTypes('auth/REGISTER');
const [LOGIN, LOGIN_SUCCESS, LOGIN_FAILURE] =
  createRequestActionTypes('auth/LOGIN');

export const changeField = createAction(
  CHANGE_FIELD,
  ({ form, key, value }) => ({
    form,
    key,
    value,
  }),
);
console.log(
  'changeField',
  changeField({
    form: 'form',
    key: 'key',
    value: 'value',
  }),
);
export const initializeForm = createAction(INITIALIZE_FORM, (form) => form);
export const initializeTempInfo = createAction(
  INITIALIZE_TEMP_INFO,
  ({ username, password }) => ({ username, password }),
);
export const setTempInfo = createAction(SET_TEMP_INFO, (type, value) => ({
  type,
  value,
}));
export const register = createAction(REGISTER, ({ username, password }) => ({
  username,
  password,
}));
export const login = createAction(LOGIN, ({ username, password }) => ({
  username,
  password,
}));

const registerSaga = createRequestSaga(REGISTER, authAPI.register);
const loginSaga = createRequestSaga(LOGIN, authAPI.login);

export function* authSaga() {
  yield takeLatest(REGISTER, registerSaga);
  yield takeLatest(LOGIN, loginSaga);
}

const initialState = {
  register: {
    username: '',
    password: '',
    passwordConfirm: '',
  },
  login: {
    username: '',
    password: '',
  },
  tempInfo: {
    username: '',
    password: '',
    nickname: null,
    profileImage: null,
  },
  auth: null,
  authError: null,
};

const auth = handleActions(
  {
    [CHANGE_FIELD]: (state, { payload: { form, key, value } }) => {
      const target = state[form];
      return {
        ...state,
        [form]: {
          ...target,
          [key]: value,
        },
      };
    },
    [INITIALIZE_FORM]: (state, payload) => {
      const { payload: form } = payload;
      console.log(state, payload);
      return {
        ...state,
        [form]: initialState[form],
      };
    },
    [INITIALIZE_TEMP_INFO]: (
      state,
      { payload: { username, password, nickname, profileImage } },
    ) => {
      return {
        ...state,
        tempInfo: {
          username,
          password,
          nickname,
          profileImage,
        },
      };
    },
    [SET_TEMP_INFO]: (state, { payload: { type, value } }) => {
      console.log(type, value, state);
      return {
        ...state,
        tempInfo: {
          ...state.tempInfo,
          [type]: value,
        },
      };
    },
  },
  initialState,
);

export default auth;
