import styled, { css } from 'styled-components';
import React from 'react';

const OptionContainer = styled.div`
  position: absolute;
  display: flex;
  z-index: 2;
  width: 150px;
  height: 40px;
  border-radius: 12px;
  background: white;
  ${({ type }) =>
    type === 'recommand'
      ? css`
          left: 110%;
        `
      : css`
          left: -95%;
        `}
`;
const Chat = styled.div`
  width: 30px;
  height: 30px;
  background-image: url('/img/chat.png');
  background-size: 80% 80%;
  background-repeat: no-repeat;
  background-position: center center;
  border: 2px solid #73b2ff;
  border-radius: 10px;
  margin: 5px;
`;
const Add = styled.div`
  width: 30px;
  height: 30px;
  background-image: url('/img/add.png');
  background-size: 80% 80%;
  background-repeat: no-repeat;
  background-position: center center;
  border: 2px solid #73b2ff;
  border-radius: 10px;
  margin: 5px;
`;
const Remove = styled.div`
  width: 30px;
  height: 30px;
  background-image: url('/img/remove.png');
  background-size: 80% 80%;
  background-repeat: no-repeat;
  background-position: center center;
  border: 2px solid #73b2ff;
  border-radius: 10px;
  margin: 5px;
`;
const OptionBox = ({ type }) => {
  return type === 'recommand' ? (
    <OptionContainer type={type}>
      <Chat title="채팅하기" />
      <Add title="친구추가" />
      <Remove title="관심없음" />
    </OptionContainer>
  ) : (
    <OptionContainer type={type}>
      <Chat title="채팅하기" />
      <div>추가</div>
      <div>삭제</div>
    </OptionContainer>
  );
};

export default OptionBox;
