import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import RecommandList from '../../components/Friends/RecommandList';
import { getRecommand } from '../../modules/friends';

const RecommandContainer = () => {
  const dispatch = useDispatch();
  const { uid, recommandList, tags } = useSelector(
    ({ user, friends: { recommandList, tagList } }) => ({
      recommandList,
      tags: tagList,
      uid: user.user.uid,
    }),
  );
  useEffect(() => {
    dispatch(getRecommand({ filter: tags, uid }));
  }, [dispatch, tags, uid]);

  const onRefresh = () => {
    dispatch(getRecommand({ filter: tags, uid }));
  };
  const friendRequest = (receiver) => {
    console.log(localStorage.user, receiver);
  };
  return (
    <RecommandList
      friendRequest={friendRequest}
      recommandList={recommandList}
      onRefresh={onRefresh}
    />
  );
};

export default RecommandContainer;
