import React from 'react';
import styled, { css } from 'styled-components';

const ChattingLogsBlock = styled.div`
  width: 100%;
  flex: 6;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`;
const Message = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  ${({ type }) =>
    type === 'send' &&
    css`
      justify-content: flex-start;
    `}
  ${({ type }) =>
    type === 'received' &&
    css`
      justify-content: flex-end;
    `}
`;
const ChattingLogs = ({ logs }) => {
  return (
    <ChattingLogsBlock>
      {logs.map(({ message, type }, idx) => (
        <Message key={idx} type={type}>
          {message}
        </Message>
      ))}
    </ChattingLogsBlock>
  );
};

export default ChattingLogs;
