import React, { useCallback, useEffect, useReducer, useState } from 'react';
import ChatHall from '../../components/common/ChatHall';
import { useSelector } from 'react-redux';
import io from 'socket.io-client';
import { socket } from '../../lib/sockets/chatHallSocket';

const SERVER = 'http://localhost:4000';
const ChatHallContainer = () => {
  const [msg, setMsg] = useState('');
  const [state, dispatch] = useReducer(reducer, initialState);
  let { nickname, profileImage } = useSelector(({ user }) => ({
    nickname: user.user ? user.user.nickname : 'guest',
    profileImage: user.user ? user.user.profileImage : '',
  }));
  useEffect(() => {
    socket.on('broadcastMsg', (data) => {
      console.log('receive data : ', data);
      dispatch({ type: 'msg', msg: { ...data, received: true } });
    });
  }, [dispatch]);

  const onChange = (e) => {
    setMsg(e.target.value);
  };
  const changeScroll = useCallback((e) => {
    e.scrollTop = e.scrollHeight;
  }, []);
  const onSubmit = () => {
    if (msg === '') return;
    socket.emit('msg', { nickname, msg: msg.trim() });
    dispatch({ type: 'msg', msg: { send: true, nickname, msg: msg.trim() } });
    setMsg('');
  };
  const onKeySubmit = (e) => {
    if (e.key === 'Enter') {
      onSubmit();
      setMsg('');
    }
  };
  console.log(dispatch({ type: 'access' }));
  return (
    <ChatHall
      changeScroll={changeScroll}
      msg={msg}
      onChange={onChange}
      onSubmit={onSubmit}
      onKeySubmit={onKeySubmit}
      msgList={state.msg}
    />
  );
};

const initialState = {
  msg: [],
  myInfo: {
    nickname: 'guest',
    profileImage: null,
  },
};
function reducer(state, action) {
  if (action.type === 'msg') {
    return { ...initialState, msg: [...state.msg, action.msg] };
  } else if (action.type === 'setting') {
    console.log('in action.type setting', state, action);
    return {
      ...initialState,
      myInfo: { nickname: action.nickname, profileImage: action.profileImage },
    };
  } else if (action.type === 'access') {
    return {
      ...state.myInfo,
    };
  } else {
    throw new Error();
  }
}

export default ChatHallContainer;
