import React, { useEffect, useState } from 'react';
import ChatHall from '../../components/common/ChatHall';
import { useSelector } from 'react-redux';
import { socket } from '../../lib/sockets/chatHallSocket';

const ChatHallContainer = () => {
  const [msg, setMsg] = useState('');
  const [msgList, setMsgList] = useState([]);
  const [submit, setSubmit] = useState(() => {});

  let { nickname, profileImage } = useSelector(({ user }) => ({
    nickname: user.user ? user.user.nickname : 'guest',
    profileImage: user.user ? user.user.profileImage : '',
  }));
  useEffect(() => {
    socket.on('broadcastMsg', (data) => {
      setMsgList([...msgList, { received: true, msg: data.msg }]);
    });
    setSubmit(() => socket.emit('msg', { nickname, msg: msg.trim() }));
  }, [nickname, msgList, msg, submit]);
  const onChange = (e) => {
    setMsg(e.target.value);
  };
  const onSubmit = () => {
    console.log('in onSubmit');
    submit();
    setMsgList([...msgList, { send: true, msg: msg.trim() }]);
    setMsg('');
  };
  const onKeySubmit = (e) => {
    if (e.key === 'Enter') {
      onSubmit();
      setMsg('');
    }
  };

  return (
    <ChatHall
      msg={msg}
      onChange={onChange}
      onSubmit={onSubmit}
      onKeySubmit={onKeySubmit}
      msgList={msgList}
    />
  );
};

export default ChatHallContainer;
