import React from 'react';
import styled, { css } from 'styled-components';

const ChatHallBlock = styled.div`
  width: 800px;
  height: 500px;
  margin: 50px auto;
  display: flex;
  justify-content: space-between;
`;
const ChatListContainer = styled.div`
  width: 65%;
  height: 95%;
  border: 1px solid black;
  position: relative;
`;
const UserList = styled.div`
  width: 30%;
  height: 95%;
  border: 1px solid black;
`;
const ChatList = styled.div``;
const ChatInputBox = styled.div`
  width: 100%;
  height: 60px;
  position: absolute;
  left: 0;
  bottom: 0px;
  display: flex;
  justify-content: space-around;
  align-items: center;
  background: rgba(0, 0, 0, 0.03);
`;
const ChatInput = styled.input`
  width: 70%;
  height: 25px;
  border-radius: 10px;
  outline: none;
  border: 1px solid black;
  padding-left: 10px;
  padding-right: 10px;
`;
const ChatButton = styled.button`
  width: 50px;
  height: 30px;
  cursor: pointer;
  background: none;
  outline: none;
  border: 2px solid black;
  border-radius: 20px;
  font-size: 1.7rem;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const Message = styled.div`
  width: 100%;
  ${(props) =>
    props.received &&
    css`
      text-align: left;
      color: blue;
    `}
  ${(props) =>
    props.send &&
    css`
      text-align: right;
      color: red;
    `}
`;

const ChatHall = ({ msgList, msg, onChange, onSubmit, onKeySubmit }) => {
  return (
    <ChatHallBlock>
      <ChatListContainer>
        <ChatList>
          {msgList.map((msg, idx) => (
            <Message key={idx} received={msg.received} send={msg.send}>
              {msg.received ? msg.nickname : ''} {msg.msg}
            </Message>
          ))}
        </ChatList>
        <ChatInputBox>
          <ChatInput value={msg} onChange={onChange} onKeyPress={onKeySubmit} />
          <ChatButton onClick={onSubmit}>+</ChatButton>
        </ChatInputBox>
      </ChatListContainer>
      <UserList></UserList>
    </ChatHallBlock>
  );
};

export default ChatHall;
