import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from '../../components/common/Header';
import Navigation from '../../components/common/Navigation';
import { initializeChatHall, leaveChatHall } from '../../modules/chatHall';
import { logout } from '../../modules/user';

const HeaderContainer = () => {
  const { user, chatHall } = useSelector(({ user, chatHall }) => ({
    user: user.user,
    chatHall: chatHall.user,
  }));
  const dispatch = useDispatch();
  const onLogout = () => {
    dispatch(logout());
    if (localStorage.chatHall) {
      dispatch(leaveChatHall({ username: chatHall.username }));
      dispatch(initializeChatHall());
      localStorage.removeItem('chatHall');
    }
  };
  return (
    <>
      <Header user={user} onLogout={onLogout} />
      {user ? <Navigation /> : ''}
    </>
  );
};

export default HeaderContainer;
