import React from 'react';
import styled, { css } from 'styled-components';
import palette from '../../lib/styles/palette';

const MessageContainer = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;
const MessageBlock = styled.div`
  ${(props) =>
    props.received &&
    css`
      text-align: left;
      color: blue;
    `}
  ${(props) =>
    props.send &&
    css`
      text-align: right;
      color: red;
    `}
    display:flex;
`;
const Nickname = styled.div`
  color: white;
  background: ${palette.blue[1]};
  padding: 10px;
  border-radius: 12px;
  margin-bottom: 10px;
  margin-right: 10px;
  text-align: center;
  line-height: 1rem;
`;
const Text = styled.div`
  background: yellow;
  line-height: 1rem;

  ${(props) => props.received && css``}
`;

const Message = ({ idx, send, received, nickname, msg }) => {
  return (
    <MessageContainer>
      {send ? <div /> : ''}
      <MessageBlock key={idx} received={received}>
        {received ? <Nickname>{nickname}</Nickname> : ''}
        <Text>{msg}</Text>
      </MessageBlock>
    </MessageContainer>
  );
};

export default Message;
