import React from 'react';
import styled from 'styled-components';

const ChattingLogsBlock = styled.div`
  width: 100%;
  flex: 6;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`;
const Message = styled.div`
  border: 1px solid black;
  width: 100px;
`;
const Send = styled(Message)`
  align-items: flex-end;
`;
const Receive = styled(Message)`
  align-items: flex-start;
`;
const ChattingLogs = ({ logs }) => {
  return (
    <ChattingLogsBlock>
      {logs.map(({ message, type }, idx) =>
        type === 'send' ? (
          <Send key={idx}>{message}</Send>
        ) : (
          <Receive key={idx}>{message}</Receive>
        ),
      )}
    </ChattingLogsBlock>
  );
};

export default ChattingLogs;
